mapper.update:
UPDATE 
	board_cafe
SET 
	title=#{title},content=#{content}
WHERE 
	num=#{num}

mapper.select:
SELECT result1.* FROM (SELECT num,writer,title,content,viewcount, TO_CHAR(regdate,'yyyy/mm/dd hh24:mi:ss')AS regdate, LAG(num,1,0) OVER(ORDER BY num DESC) AS prevNum, LEAD(num,1,0) OVER(ORDER BY num DESC) AS nextNum FROM board_cafe
WHERE title LIKE '%'||#{title}||'%' OR content LIKE '%'||#{content}||'%'
WHERE title LIKE '%'||#{title}||'%'
WHERE writer LIKE '%'||#{writer}||'%'
)result1 WHERE num=#{num}
