mapper.select:
SELECT id 
FROM users
WHERE id=#{inputId}

SELECT id
FROM users
WHERE id=#{id} AND pwd=#{pwd}

SELECT id,pwd,email,profile,regdate,warnnum
FROM users
WHERE id=#{id}

mapper.update:
UPDATE users
SET email=#{email},profile=#{profile}
WHERE id=#{id}

UPDATE users
SET pwd=#{newPwd}
WHERE id=#{id} AND pwd=#{pwd}

UPDATE users
SET warnnum=warnnum+1
WHERE id=#{id}
